import React from 'react'
// import { ParallaxLayer } from "@react-spring/web";
// import { Parallax } from 'react-parallax'
import { useState, useEffect } from 'react'
import img1 from '../images/bg2.jpg'
import img2 from '../images/bg3.jpg'
import './Title.css'

function Title() {
  const [scrollPosition, setScrollPosition] = useState(0)
  const factor = 5 // adjust this to control the speed of the translation
  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY)
    }

    window.addEventListener('scroll', handleScroll)

    return () => {
      window.removeEventListener('scroll', handleScroll)
    }
  }, [])
  return (
    <>
      <div
        className='title_container'
        style={{ transform: `translateY(-${scrollPosition * factor}px)` }}
      >
        <div className='img1'>
          <img src={img1} alt='bg1' />
        </div>

        <div className='title mx-20 font-semibold'>
          Walchand Linux Users' Group
          <br />
          <p className='presents'>Presents</p>
          <div
            className='techno_heading'
            style={{ fontFamily: "'Russo One', sans-serif" }}
          >
            TechnoTweet 2k23
          </div>
        </div>
        <div className='img2'>
          <img src={img2} alt='bg2' />
        </div>
      </div>
    </>
  )
}

export default Title
