import React from 'react'
// import { ParallaxLayer } from "@react-spring/web";
// import { Parallax } from 'react-parallax'
import { useState, useEffect } from 'react'
import img1 from '../images/techno1-removebg-preview.png'
import img2 from '../images/techno12.png'
import './Title.css'

function Title() {
  const [scrollPosition, setScrollPosition] = useState(0)
  const threshold = 500 // adjust this to control where the element stops
  const factor = 4

  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY)
    }

    window.addEventListener('scroll', handleScroll)

    return () => {
      window.removeEventListener('scroll', handleScroll)
    }
  }, [])

  const translateY = scrollPosition > threshold ? scrollPosition : 0

  return (
    <>
      <div className='title_container h-screen'>
        <div
          className='img1'
          style={{ transform: `translateY(${scrollPosition * factor}px)` }}
        >
          <img src={img1} alt='bg1' />
        </div>

        <div
          className='title mx-20 font-semibold'
          style={{ transform: `translateY(-${scrollPosition * factor}px)` }}
        >
          Walchand Linux Users' Group
          <br />
          <p className='presents'>Presents</p>
          <div
            className='techno_heading'
            style={{ fontFamily: "'Russo One', sans-serif" }}
          >
            TechnoTweet 2k23
          </div>
        </div>
        <div
          className='img2'
          style={{ transform: `translateY(${scrollPosition * factor}px)` }}
        >
          <img src={img2} alt='bg2' />
        </div>
      </div>
    </>
  )
}

export default Title
