import React from 'react'
// import { ParallaxLayer } from "@react-spring/web";
// import { Parallax } from 'react-parallax'
import { useState, useEffect, useRef } from 'react'
import img1 from '../images/bg2.jpg'
import img2 from '../images/bg3.jpg'
import './Title.css'

function Title() {
  const [scrollPosition, setScrollPosition] = useState(0)
  const [hasReachedEnd, setHasReachedEnd] = useState(false)
  const factor = 4
  const contentRef = useRef(null)

  const threshold = 50 // adjust this to control where the element stops

  useEffect(() => {
    const handleScroll = () => {
      setScrollPosition(window.scrollY)
    }

    if (!hasReachedEnd) {
      window.addEventListener('scroll', handleScroll)
    }

    return () => {
      window.removeEventListener('scroll', handleScroll)
    }
  }, [hasReachedEnd])

  useEffect(() => {
    const contentHeight = contentRef.current.clientHeight
    const windowHeight = window.innerHeight

    if (scrollPosition > contentHeight - windowHeight) {
      setHasReachedEnd(true)
    }
  }, [scrollPosition])

  const translateY = scrollPosition > threshold ? scrollPosition - threshold : 0

  return (
    <>
      <div className='title_container'>
        <div ref={contentRef}>
          {/* conditionally render content after contentRef has been set */}
          {contentRef.current && (
            <div
              className='img1'
              style={{ transform: `translateY(${translateY * factor}px)` }}
            >
              <img src={img1} alt='bg1' />
            </div>
          )}
        </div>

        <div className='title mx-20 font-semibold'>
          Walchand Linux Users' Group
          <br />
          <p className='presents'>Presents</p>
          <div
            className='techno_heading'
            style={{ fontFamily: "'Russo One', sans-serif" }}
          >
            TechnoTweet 2k23
          </div>
        </div>
        <div className='img2'>
          <img src={img2} alt='bg2' />
        </div>
      </div>
    </>
  )
}

export default Title
